mapper.select:
select * from equipmentManagement 
where equipment_no=#{equipment_no}
and status='대기'

mapper.update:
update equipmentManagement
set status='대기', e_repair_type=#{e_repair_type},count=#{count},cost=#{cost},total=#{total},repair_reason=#{repair_reason},report_date=#{report_date}
where equipment_no=#{equipment_no}

update equipmentManagement
set e_repair_type='정상'
where equipment_no=#{equipment_no}
