mapper.select:
		SELECT *, CONCAT(
	        SUBSTRING(mem_phone, 1, 3), '-',
	        SUBSTRING(mem_phone, 4, 4), '-',
	        SUBSTRING(mem_phone, 8, 4)
   			) AS formatted_mem_phone, FLOOR(DATEDIFF(CURDATE(), mem_birth) / 365.25) AS mem_age FROM member
		where mem_no = #{mem_no};

mapper.update:
  	 	update member set mem_name = #{mem_name}, mem_birth=#{mem_birth}, mem_phone=#{mem_phone},
		sms_opt=#{sms_opt}, mem_email=#{mem_email}, email_opt=#{email_opt}, mem_note=#{mem_note},
		mem_addr1=#{mem_addr1}, mem_addr2=#{mem_addr2}
		where mem_no = #{mem_no};

  	 	update member set mem_delete = now()
		WHERE mem_no IN

	        #{mem_no}

mapper.select:
select name, m.mem_no, r.registration_date, field_code, description,level_code, class_name,division_code,max_capacity,instructor_no,min_capacity, start_date, end_date, recurrence_days, start_time_code, end_time_code from registration r join member m on m.mem_no = r.mem_no join classSchedule cs on cs.schedule_id = r.schedule_no join class c on c.class_no = cs.class_no join employee e on instructor_no = user_no
m.mem_no = #{mem_no}
and field_code = #{fieldCode} and status = #{status}
and status = #{status}
limit #{pageNormal}, 1
limit #{pageRecruit}, 1

mapper.update:
update healthMonitor set exercise_goal = #{exercise_goal},
height = #{height}, weight = #{weight}, fat=#{fat}, fat_mass=#{fat_mass},
muscle=#{muscle}, muscle_mass=#{muscle_mass}, belly_fat=#{belly_fat}, bmi=#{bmi},
bmr=#{bmr}, consult_content=#{consult_content}
where inbody_no = #{inbody_no} and manager_no = #{manager_no};
