mapper.select:
		SELECT * FROM member
    	WHERE (mem_name LIKE CONCAT('%', #{keyword}, '%')
        OR mem_phone LIKE CONCAT('%', #{keyword}, '%')
        OR mem_email LIKE CONCAT('%', #{keyword}, '%'))

        AND reg_date LIKE CONCAT(#{memYear}, '%')


        AND mem_rank = #{filter}


        AND mem_gender = #{mem_gender}


    ORDER BY mem_no DESC
    LIMIT #{pageStart}, #{pageSize}

SELECT count(mem_no) FROM member WHERE (mem_name LIKE CONCAT('%', #{keyword}, '%') OR mem_phone LIKE CONCAT('%', #{keyword}, '%') OR mem_email LIKE CONCAT('%', #{keyword}, '%'))
AND reg_date LIKE CONCAT(#{memYear}, '%')
AND mem_rank = #{filter}
AND mem_gender = #{mem_gender}
