mapper.select:
select * from equipmentManagement 
where equipment_no=#{equipment_no}
and status='대기'

mapper.update:
UPDATE equipmentManagement SET e_repair_type = CASE WHEN e_repair_type = '구입' THEN '정상' WHEN e_repair_type = '추가구입' THEN '정상' WHEN e_repair_type = '수리' THEN '수리중' ELSE '폐기' END, approval_date = NOW(), status = '승인'
,count = count + #{addcount} ,total = total+(#{addcount} * cost)
WHERE equipment_no = #{equipment_no}

update equipmentManagement
set status='대기', e_repair_type=#{e_repair_type},count=#{count},addcount=#{addcount},cost=#{cost},total=#{count}*#{cost},repair_reason=#{repair_reason},report_date=#{report_date}
where equipment_no=#{equipment_no}

update equipmentManagement
set e_repair_type='정상'
where equipment_no=#{equipment_no}

UPDATE equipmentManagement
e_repair_type = '정상', status ='승인', addcount=0
e_repair_type = '정상', status ='승인'
e_repair_type = '정상', status ='승인'
e_repair_type=#{e_repair_type}
where equipment_no=#{equipment_no}
