mapper.select:
select * from equipmentManagement
WHERE (equipment_name like CONCAT('%', #{keyword}, '%') OR manufacturer LIKE CONCAT('%', #{keyword}, '%'))
WHERE (equipment_name like CONCAT('%', #{keyword}, '%'))
WHERE (manufacturer LIKE CONCAT('%', #{keyword}, '%'))
AND status ='반려'
AND status ='승인'
AND field = #{field}
AND e_repair_type = #{e_repair_type}
ORDER BY equipment_no DESC LIMIT #{pageStart}, #{pageSize}

select * from facilityManagement
where repair_type=#{repair_type} and facility_no=#{facility_no}
ORDER BY repair_date DESC
LIMIT #{pageStart}, #{pageSize}

SELECT c.class_no, c.class_name, c.description, c.field_code, c.division_code, c.level_code, c.min_capacity, c.max_capacity, c.instructor_no, e.name AS instructor_name FROM class c LEFT JOIN instructor i ON c.instructor_no = i.instructor_no LEFT JOIN employee e ON i.user_no = e.user_no
WHERE (c.class_name like CONCAT('%', #{keyword}, '%') OR e.name LIKE CONCAT('%', #{keyword}, '%'))
WHERE (c.class_name like CONCAT('%', #{keyword}, '%'))
WHERE (e.name like CONCAT('%', #{keyword}, '%'))
AND field_code = #{field}
AND division_code = #{division}
order by c.class_no desc limit #{pageStart}, #{pageSize}

SELECT count(c.class_no) FROM class c LEFT JOIN instructor i ON c.instructor_no = i.instructor_no LEFT JOIN employee e ON i.user_no = e.user_no
(c.class_name like CONCAT('%', #{keyword}, '%') OR e.name LIKE CONCAT('%', #{keyword}, '%'))
c.class_name like CONCAT('%', #{keyword}, '%')
e.name like CONCAT('%', #{keyword}, '%')
AND field_code = #{field}
AND division_code = #{division}

SELECT *
FROM classSchedule
WHERE class_no = #{classNo}

AND status = #{status}

ORDER BY start_date ASC;
